INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(103032, 'esES', '<hip>', '', 22566),
(92648, 'esES', '¡La Piedramar será nuestra!', '', 22566),
(114475, 'esES', 'Ayúdame, criatura...', '', 22566),
(92796, 'esES', 'Deberíamos volver con el abuelo.', '', 22566),
(93256, 'esES', 'Una vez fui un elfo noble, dragón. Conozco el significado del honor.', '', 22566),
(93255, 'esES', 'Entonces usa a los vástagos. Pero si me traicionas, prepárate para sufrir la ira del Vuelo.', '', 22566),
(93254, 'esES', 'Solo quiero ayudar, amigo dragón.', '', 22566),
(93253, 'esES', 'Pero parece que no tenemos elección.', '', 22566),
(93252, 'esES', 'No confío en los Caídos de la Noche...', '', 22566),
(102012, 'esES', 'Entonces, toma. ¡Pruébalo!', '', 22566),
(102011, 'esES', 'Metomentodo... Estás aquí para robar mi maná, ¡¿verdad?!', '', 22566),
(92879, 'esES', '¿Quién es este?', '', 22566),
(92874, 'esES', 'Mira, te lo demostraré. No soy solo un bicho raro esclavo del maná como esos desgraciados.', '', 22566),
(92871, 'esES', 'Espero que puedas ver más allá de mis harapos y mi comportamiento desesperado. No soy un monstruo.', '', 22566),
(92869, 'esES', 'Me rindo. Por favor, perdóname. Lo lamento muchísimo.', '', 22566),
(93001, 'esES', '¡Hay que tratar a estos cuatro vástagos con cristales Ley de inmediato!', '', 22566),
(93000, 'esES', 'Los Caídos de la Noche han atacado el criadero.', '', 22566),
(92999, 'esES', '¡Mensaje urgente para Senegos!', '', 22566),
(92795, 'esES', 'No me llores aún, mi luz estelar. Déjame vivir esta última aventura.', '', 22566),
(92794, 'esES', '', 'Ahora descansa, abuelo. No malgastes tu energía.', 22566),
(102279, 'esES', 'No lo sé... Pero sí sé dónde están las piezas.', '', 22566),
(102278, 'esES', 'La Piedramar... ¿Destrozada? ¿Cómo?', '', 22566),
(102277, 'esES', 'Hablas de la Piedramar de Golganneth. Hace mucho que quedó destrozada.', '', 22566),
(102276, 'esES', 'Senegos, buscamos uno de los Pilares de la Creación. Sé que está en Azsuna.', '', 22566),
(101344, 'esES', '¿Puedo ayudarte en algo, $c?', '', 22566),
(101350, 'esES', 'Ojalá pudiese hacer algo más por Senegos.', '', 22566),
(92694, 'esES', '<El gran dragón respira de forma entrecortada. Está sufriendo.>', '', 22566),
(92791, 'esES', 'Joven, acércate. No estoy tan muerto como parezco.', '', 22566),
(95092, 'esES', '¿Qué está pasando aquí?', '', 22566),
(92346, 'esES', 'Esto no tiene buena pinta. No está bien.', '', 22566),
(121773, 'esES', '', 'Gracias por salvarnos en Faronaar.\n\nNo me puedo creer que Cyana nos traicionara a todos, pero hay otros que también se han convertido en jurapenas. Les daremos caza y los mataremos junto a sus nuevos amos de la Legión.\n\n¡Lo juro!', 22566),
(106190, 'esES', 'Al rescatar a Stellagosa has conseguido un poderoso amigo y aliado.\n\nMe alivia que el Vuelo Azul haya sobrevivido, aunque a duras penas. Debemos ir al Reposo Alazur y hablar con ese tal Senegos.\n\nSi alguien puede indicarnos el camino correcto para localizar el Pilar de la Creación, seguramente sea él.', '', 22566),
(106165, 'esES', '', 'Debes venir a Reposo Alazur y reunirte con mi abuelo, Senegos.', 22566),
(93012, 'esES', '', 'No podemos dejar que se recuperen o las Islas Abruptas estarán perdidas.', 22566),
(93009, 'esES', '', 'Les hemos asestado un buen golpe, pero la Legión está invocando demonios aún más poderosos.', 22566),
(108426, 'esES', '', '¡No permitiremos la existencia de traidores jurapenas al servicio de la Legión!', 22566),
(106123, 'esES', '', 'Estamos preparadas.', 22566),
(106122, 'esES', '', 'Ja, buena suerte. Gujasombría ha sufrido ciertos \"cambios\".', 22566),
(92980, 'esES', '', 'Stellagosa, no... ¡Vuelve! ¡Maldición!', 22566),
(121776, 'esES', '', '$n, la Legión Ardiente nos está enviando a los demonios más poderosos de su arsenal. No piensan perder esta tercera guerra y no se detendrán ante nada hasta que todos desaparezcamos de la faz de Azeroth.\n\nNo temas, les devolveremos el favor.', 22566),
(104260, 'esES', 'Tú, Kor\'vas y Cyana, atacad a la Legión desde dentro. Después los aplastaremos juntos.', '', 22566),
(92230, 'esES', '', '$gJuntos:Juntas; liberaremos las almas de los demonios de las gemas y utilizaremos la guadaña para obligarlos a hablar.\n\nAvísame cuando estés $glisto:lista; para empezar.', 22566),
(90215, 'esES', '', 'Entonces prueba la Guadaña de Almas...', 22566),
(88938, 'esES', '', '¡Dime, demonio! ¿Dónde guarda la Legión el cuerpo de Illidan?', 22566),
(104256, 'esES', 'Me alegro de que estés aquí. Malevolencia nos ha estado facilitando el transporte de ida y vuelta al Martillo Vil.\n\nLa Legión Ardiente ha invadido Faronaar al otro lado del istmo. Debemos destruir su ejército antes de que invada el resto de la isla.', '', 22566),
(105747, 'esES', 'La Legión no olvidará este día.', '', 22566),
(116323, 'esES', 'Esto no ha terminado... Illidari.', '', 22566),
(97107, 'esES', '¡Con mucho gusto!', '', 22566),
(116293, 'esES', '¡Usa las Hojas Gemelas y destruye al intruso!', '', 22566),
(114229, 'esES', 'No me falles, Varedis.', '', 22566),
(98056, 'esES', 'Lamento la muerte de tus aliados. No te preocupes... ¡Aprovecharé bien sus almas!', '', 22566),
(97023, 'esES', '¡MUERE!', '', 22566),
(104141, 'esES', '¡Renuncia a la vida!', '', 22566),
(96912, 'esES', 'Has ido demasiado lejos, mortal. Esto acaba aquí.', '', 22566),
(96866, 'esES', '¡Bañadlos en fuego infernal! ¡Detened su avance!', '', 22566),
(96896, 'esES', '¡Terminad el portal, maldita sea! ¡Debemos proteger los resguardos!', '', 22566),
(97374, 'esES', '¿Deseas morir? ¿Osas enfrentarte a la cólera de mi señor sin ayuda?', '', 22566),
(105746, 'esES', 'Esos necios creen que pueden contigo. ¡Que paguen el precio de su arrogancia!', '', 22566),
(96855, 'esES', '¡A las armas! No deben molestar a lord Alma Vil.', '', 22566),
(105745, 'esES', '¡Al infierno con el aterrizaje! ¡Irrumpe en la fortaleza y muéstrales tu poder!', '', 22566),
(105744, 'esES', 'Te han detectado. ¡Date prisa!', '', 22566),
(105743, 'esES', '¡Maldición! Tendrás que destruir los resguardos que mantienen esa barrera antes de enfrentarte a Varedis.', '', 22566),
(105774, 'esES', 'No bajéis la guardia. Las habilidades de Varedis son legendarias.', '', 22566),
(116017, 'esES', '¿Puedo ayudarte? ¿Quieres ver el último informe de exploración o conocer la actividad más reciente de la Legión en Azeroth?', '', 22566),
(113079, 'esES', '¿Crees que no sabemos lo que pretendes?', '', 22566),
(118529, 'esES', '', 'Hola, corazón. Siempre es un placer volver a verte.\n\n¿En qué puedo ayudarte?', 22566),
(114181, 'esES', 'Reunirnos a todos aquí ha sido todo un logro por tu parte. Con los cambios que estás aplicando a la forma de operar de los Illidari, sin duda podemos ganar la guerra.\n\nCuando esto haya acabado, la Legión Ardiente ya no será una amenaza y nuestros sacrificios habrán valido la pena.', '', 22566),
(112971, 'esES', 'El alma de Illidan ha hablado. El camino está claro.', '', 22566),
(96992, 'esES', '', 'Regresa a tus otras tareas de momento. Pronto nos enfrentaremos al propio lord Cresta Cuervo.', 22566),
(96990, 'esES', 'Me niego a creerlo. Lord Cresta Cuervo no puede estar colaborando con Gul\'dan.', '', 22566),
(96704, 'esES', '', 'Es inútil, hermano. Al igual que los otros no-muertos, no reconoce nuestra naturaleza.', 22566),
(96703, 'esES', '¿Cresta Cuervo? ¡Imposible! Y no somos demonios, ¿no me reconoces?', '', 22566),
(96702, 'esES', 'Lord Cresta Cuervo me otorgó este puesto. ¡No os lo entregaré, demonios!', '', 22566),
(96692, 'esES', 'Siempre te tuviste en mucha estima. Bien, será otra baja innecesaria, si insistes.', '', 22566),
(94967, 'esES', 'El ejército de lord Cresta Cuervo eliminará a los invasores de estas tierras.', '', 22566),
(94908, 'esES', '', 'Hay que detener a Gul\'dan. Me opondré a la Legión mientras viva.', 22566),
(96578, 'esES', '¡Es el arma de Maiev! ¡Ha estado aquí!', '', 22566),
(96576, 'esES', 'Este tuvo un aciago final. No hay rastro de Maiev.', '', 22566),
(96600, 'esES', 'Conozco a mi hermana. Nunca cometería esos crímenes a menos que estuviese bajo algún tipo de influencia infame.$B$BAun así, en vez de seguir investigando, asumí lo peor.$B$BHe sido un mal hermano, pero todavía tengo tiempo de hacer las cosas bien.', '', 22566),
(96599, 'esES', 'Asesinó a inocentes con la esperanza de cortar los lazos que nuestro pueblo mantenía con la Alianza.$B$BAun así, no fui capaz de matarla.$B$BPrimero pensé que había sido débil, que el amor me había impedido hacer lo que era necesario.', '', 22566),
(97196, 'esES', 'Una tumba profanada, demonios en el Torreón Grajo Negro y elfos de la noche enemigos... No puede ser una coincidencia.$B$BLa Legión ha resucitado a lord Cresta Cuervo. Mi antiguo maestro vuelve a estar vivo.', '', 22566),
(97378, 'esES', '', '¿D... d... dónde está todo el mundo?', 22566),
(96181, 'esES', '', 'Ven, acércate y déjame echarte un vistazo.$B$B¡Ah! ¿$gUn:Una; $c, en estas tierras tan apartadas? ¡Madre mía!', 22566),
(107644, 'esES', 'Esos invasores no-muertos son duros de roer, pero Marl y yo no permitiremos que nos arrebaten la granja.', '', 22566),
(94686, 'esES', '', 'Sin este maíz, la aldea morirá. Pero no te preocupes, cariño, ¡tengo la zona bloqueada!', 22566),
(97342, 'esES', 'Mi pueblo ha sufrido demasiado. Fue el alzamiento de la Plaga lo que nos obligó a huir de Gilneas hace muchos años.$B$BAhora, criaturas no muertas se alzan en la fortaleza del norte.$B$BDefenderé este pueblo hasta nuestro aciago final.', '', 22566),
(96101, 'esES', 'No soy tu enemigo, alcalde. Busco a alguien en el Torreón Grajo Negro.', '', 22566),
(102771, 'esES', '¡Cállate, gusano! Pronto me encargaré de ti.', '', 22566),
(102770, 'esES', 'Eso dice la sombra de Xavius.', '', 22566),
(98620, 'esES', '¿Quieres volar? Tendrás la montura más rápida.', '', 22566),
(32779, 'esES', '¿Cómo puedo ayudarte?', '¿Cómo puedo ayudarte?', 22566),
(98814, 'esES', 'Ver misiones disponibles.', 'Ver misiones disponibles.', 22566),
(106257, 'esES', 'La sangre vil echa espuma con violencia, un remolino de formas ofuscadas hierve bajo la superficie.', 'La sangre vil echa espuma con violencia, un remolino de formas ofuscadas hierve bajo la superficie.', 22566),
(120065, 'esES', 'Efectos de sonido para las tropas.', '', 22566),
(122032, 'esES', '', 'Somos lo único que se interpone entre la Legión y la aniquilación.', 22566),
(117329, 'esES', '¡No luchas a solas!', '', 22566),
(109232, 'esES', 'Hoy ha conseguido las legendarias Matarreyes y, al hacerlo, nos ha permitido golpear directamente a la Legión.', '', 22566),
(99197, 'esES', '', 'Structural integrity at 0%%!', 22566),
(32780, 'esES', '¿Cómo puedo ayudarte?', '¿Cómo puedo ayudarte?', 22566),
(93537, 'esES', '', 'Ojalá.', 22566),
(93536, 'esES', '¿Alguien de fuera? ¡Espero que sea un asesino y que acabe por fin con ese príncipe!', '', 22566),
(92421, 'esES', '¡Se aproxima $gun forastero:una forastera;!', '', 22566),
(92647, 'esES', '¡Buscad por todas partes! ¡Convertid las ruinas en escombros!', '', 22566),
(114474, 'esES', '¡Sí, sí! Entrega tu poder, dragón. ¡Ahora estas pozas pertenecen a los Nocheterna!', '', 22566),
(102025, 'esES', 'Escoria miserable. No sabes a quién te estás enfrentando. Ja... Ja... ¡Jaaaa, ja, ja, ja, jaaaa!', '', 22566),
(102024, 'esES', 'Espera, ¿qué haces? ¡Mis pilones!', '', 22566),
(102023, 'esES', 'No importa. ¡Pronto todos os arrodillaréis ante el nuevo Príncipe de Azsuna!', '', 22566),
(102021, 'esES', '¿Te ha enviado ese dragón? ¿O ha sido ese mendigo, Runas?', '', 22566),
(102013, 'esES', '¿Cargándote a mis tenientes? Supongo que te crees inteligente.', '', 22566),
(102014, 'esES', 'Ishkaneth, idiota. Has derramado tu sangre y mi maná.', '', 22566),
(92855, 'esES', 'Ah, $gamigo mío:amiga mía;. Mi fiable y $gbuen amigo:buena amiga; $n.$B$BNo tendrás por casualidad cristales de maná, ¿verdad?$B$BEscucha, $gamigo mío:amiga mía;. Si alguna vez necesitas cualquier cosa, e insisto, CUALQUIER COSA, solo tienes que venir a hablar con tu leal amigo Runas. Yo haré que tus problemas desaparezcan. Puedes confiar en mí.$B$BPor supuesto, si yo necesito algo, supongo que me ayudarás, ¿verdad? Por ejemplo, si necesito un cristal de maná o algo parecido, ¿me darás uno?', '', 22566),
(92882, 'esES', 'Lo entiendo, dragón. Mi gente se ha ganado tu recelo. Permíteme compensar sus errores.', '', 22566),
(92881, 'esES', 'Un Caído de la Noche. No me fío de vosotros.', '', 22566),
(92880, 'esES', 'Me llamo Runas, hijo de Indarril, procedente de Suramar. Un placer conocerte.', '', 22566),
(92870, 'esES', 'Alimentarme de una energía tan pura... me ha nublado el juicio.', '', 22566),
(92798, 'esES', '<Ante ti se alza... no el propio Senegos, sino su proyección mágica. Parece fuerte, confiado y alegre. No es el dragón decrépito que viste languideciendo en una poza.>', '', 22566),
(111307, 'esES', 'Haré lo que pueda por Senegos, pero te ruego que actúes con rapidez, $n. Temo que no sobreviva mucho más tiempo.\n\nHay otros asuntos lejos de aquí que requieren mi atención y debo partir pronto. No obstante, volveré en cuanto pueda.', '', 22566),
(93265, 'esES', 'Por favor... ayúdame...', '', 22566),
(93263, 'esES', 'Sé por qué estás aquí, criatura...', '', 22566),
(106120, 'esES', '', 'Te doy las gracias. Ahora voy a acabar con el carcelero que me hizo esto.', 22566),
(104262, 'esES', 'Me encargaré de que la Legión no avance más.', '', 22566),
(90225, 'esES', 'Quema... ¡Quema!', '', 22566),
(90212, 'esES', '¡No... te lo... diré!', '', 22566),
(121770, 'esES', 'Mientras tú lideras a tus fuerzas Illidari en su lucha contra los demonios, yo meditaré cuál debe ser nuestro siguiente paso aquí en Azsuna.\n\nDebemos localizar el Pilar de la Creación. Sé que tiene que estar por aquí.', '', 22566),
(121775, 'esES', '', '$n, ¿cómo va la pelea? ¿Necesitas suministros o que te repare algo?', 22566),
(105748, 'esES', 'Hemos despejado el cielo eliminando a los peores. Deberíamos tener via libre hasta Dalaran desde aquí.', '', 22566),
(116324, 'esES', '¡No necesito armamentos mortales!', '', 22566),
(114230, 'esES', 'Vuelve a Argus. Ya nos encargaremos de esto otro día.', '', 22566),
(97082, 'esES', 'Bien, Illidari, ¿quieres mi cabeza para apoderarte de las Hojas Gemelas? Permíteme que te las presente... personalmente.', '', 22566),
(97137, 'esES', '¡Presencia el poder infinito de las Hojas Gemelas!', '', 22566),
(96929, 'esES', 'Mi maestro me ha otorgado muchos dones. ¡Contempla el poder del Impostor!', '', 22566),
(96928, 'esES', 'Han pasado muchos años. Deberías tener la sensatez de no enfrentarte a mí. ', '', 22566),
(97012, 'esES', '¡Mo\'arg, al frente! Esto ya ha durado demasiado.', '', 22566),
(113178, 'esES', 'Rápido, usa Visión espectral e intenta encontrarlo.', '', 22566),
(113177, 'esES', '¡Maldito diablillo escurridizo! Fue hacia el sudeste y luego se volvió invisible.', '', 22566),
(113080, 'esES', '', 'Los Illidari se creen intocables. Pronto entenderán su error.', 22566),
(88634, 'esES', 'Juntos podremos volver a sellar la Tumba de Sargeras y derrotar a la Legión Ardiente.', '', 22566),
(115612, 'esES', 'Si lo deseas, puedo ayudarte a alterar el poder de tus hojas.\n\n¡Pero cuidado! Algo se perderá en un cambio así.', '', 22566),
(89233, 'esES', '', '¿Necesitas a alguien pa\' un trabajo? Yo te conseguiré a un par.', 22566),
(112979, 'esES', 'Ya tenemos a quién seguir. Illidari, ¡saludad!', '', 22566),
(102274, 'esES', '<Al otro lado del rastrillo puedes ver una serie de túneles que llevan al Cementerio Cresta Cuervo.>', '', 22566),
(96701, 'esES', '', 'Nunca se me dio bien suplicar, Ojo de Estrella. Ya deberías saberlo.', 22566),
(96691, 'esES', '¿Cómo osáis atacar a un noble kaldorei? Vais a suplicar piedad... y no la voy a tener.', '', 22566),
(122650, 'esES', '<El cuerpo de Cassiel Nochespina está casi irreconocible. Lo torturaron y después murió a causa de las heridas.>', '', 22566),
(122649, 'esES', '', '$n, eres tú.\n\nSeguíamos a un peligroso Señor del Terror. Por desgracia, nos llevó directamente a una trampa.', 22566),
(96577, 'esES', '\"A pesar de nuestros esfuerzos por sonsacarle información, la celadora se sigue resistiendo\". ¿Se referirán a Maiev?', '', 22566),
(96598, 'esES', 'Maiev y yo elegimos caminos muy diferentes en esta vida.$B$BPoco después de la primera derrota de la Legión en Azeroth, ella dedicó su vida a buscar la justicia.$B$BAlgunos dirían que en ocasiones ha llegado demasiado lejos en esa búsqueda.', '', 22566),
(118666, 'esES', '¿Estás $glisto:lista; para salir hacia el Torreón Grajo Negro?', '', 22566),
(96355, 'esES', 'Mi hija lo es todo para mí. No te puedo agradecer lo suficiente que me la hayas traído a casa sana y salva.', '', 22566),
(96353, 'esES', '', 'Ya sabes, antes me llamaban \"Marlene la Mala\". Podía acertarle al ojo de una aguja a cien pasos.', 22566),
(122286, 'esES', '¡Pues vaya! Cómo han cambiado las cosas.', '', 22566),
(94720, 'esES', 'Ten cuidado. Da un paso en falso y te encadenaremos.', '', 22566),
(96102, 'esES', 'Basta de mentiras, elfo de la noche. Te quedarás donde pueda controlarte.', '', 22566),
(123354, 'esES', 'La Arboleda es un lugar peligroso, ¿quieres que te saque de aquí?', '', 22566),
(102777, 'esES', 'Bravas palabras para un prisionero. ¡Incluso un eco de mi poder bastó para dominarte!', '', 22566),
(102776, 'esES', 'Una amenaza vacía. No tienes poder en este reino, Señor de la Pesadilla.', '', 22566),
(102775, 'esES', '¡Una pena que hayáis llegado tan lejos para morir a manos de Xavius!', '', 22566),
(102774, 'esES', '¡Silencio, enclenque! Si vuelves a abrir la boca, ¡será para suplicar piedad!', '', 22566),
(102773, 'esES', 'No tienen nada que temer, pues tu verdadero poder reside en la Pesadilla.', '', 22566),
(102772, 'esES', 'Cuestión de suerte. Disfrutad, pronto os las veréis conmigo.', '', 22566),
(102769, 'esES', 'Aquellos que me desafían se precipitan hacia una muerte agonizante.', '', 22566),
(116346, 'esES', 'Los Tábidos te saludan.', 'Los Tábidos te saludan.', 22566),
(120162, 'esES', '¡Se abren portales! ¡Aquí vienen!', '', 22566);
